mapper.insert:
insert into t_itemkeyword (
    productcategory,
    keyword,
    countkeyword
)
values (
    #{productCategory},
    #{keyword},
    #{countKeyword}
    )

mapper.select:
select countkeyword from t_itemkeyword


        productcategory = #{productCategory}



        and keyword = #{keyword}

mapper.update:
update t_itemkeyword set
countkeyword = #{countKeyword}


        productcategory = #{productCategory}



        and keyword = #{keyword}
